import { Component, OnInit, Inject, Optional } from '@angular/core';
import { TypeofExpr } from '@angular/compiler';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { CivilianAirportDB } from '../civilianairport-db'
import { CivilianAirportService } from '../civilianairport.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-civilianairport-sorting',
  templateUrl: './civilianairport-sorting.component.html',
  styleUrls: ['./civilianairport-sorting.component.css']
})
export class CivilianAirportSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of CivilianAirport instances that are in the association
  associatedCivilianAirports = new Array<CivilianAirportDB>();

  constructor(
    private civilianairportService: CivilianAirportService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of civilianairport instances
    public dialogRef: MatDialogRef<CivilianAirportSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getCivilianAirports()
  }

  getCivilianAirports(): void {
    this.frontRepoService.pull(this.dialogData.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let civilianairport of this.frontRepo.CivilianAirports_array) {
          let ID = this.dialogData.ID
          let revPointerID = civilianairport[this.dialogData.ReversePointer as keyof CivilianAirportDB] as unknown as NullInt64
          let revPointerID_Index = civilianairport[this.dialogData.ReversePointer + "_Index" as keyof CivilianAirportDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedCivilianAirports.push(civilianairport)
          }
        }

        // sort associated civilianairport according to order
        this.associatedCivilianAirports.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedCivilianAirports, event.previousIndex, event.currentIndex);

    // set the order of CivilianAirport instances
    let index = 0

    for (let civilianairport of this.associatedCivilianAirports) {
      let revPointerID_Index = civilianairport[this.dialogData.ReversePointer + "_Index" as keyof CivilianAirportDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedCivilianAirports.forEach(
      civilianairport => {
        this.civilianairportService.updateCivilianAirport(civilianairport, this.dialogData.GONG__StackPath)
          .subscribe(civilianairport => {
            this.civilianairportService.CivilianAirportServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer + ' done');
  }
}
