// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { CivilianAirportDB } from '../civilianairport-db'
import { CivilianAirportService } from '../civilianairport.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-civilianairportstable',
  templateUrl: './civilianairports-table.component.html',
  styleUrls: ['./civilianairports-table.component.css'],
})
export class CivilianAirportsTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of CivilianAirport instances
  selection: SelectionModel<CivilianAirportDB> = new (SelectionModel)
  initialSelection = new Array<CivilianAirportDB>()

  // the data source for the table
  civilianairports: CivilianAirportDB[] = []
  matTableDataSource: MatTableDataSource<CivilianAirportDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.civilianairports
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (civilianairportDB: CivilianAirportDB, property: string) => {
      switch (property) {
        case 'ID':
          return civilianairportDB.ID

        // insertion point for specific sorting accessor
        case 'Lat':
          return civilianairportDB.Lat;

        case 'Lng':
          return civilianairportDB.Lng;

        case 'Name':
          return civilianairportDB.Name;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (civilianairportDB: CivilianAirportDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the civilianairportDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += civilianairportDB.Lat.toString()
      mergedContent += civilianairportDB.Lng.toString()
      mergedContent += civilianairportDB.Name.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private civilianairportService: CivilianAirportService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of civilianairport instances
    public dialogRef: MatDialogRef<CivilianAirportsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.civilianairportService.CivilianAirportServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getCivilianAirports()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
      ]
      this.selection = new SelectionModel<CivilianAirportDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getCivilianAirports()

    this.matTableDataSource = new MatTableDataSource(this.civilianairports)
  }

  getCivilianAirports(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.civilianairports = this.frontRepo.CivilianAirports_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let civilianairport of this.civilianairports) {
            let ID = this.dialogData.ID
            let revPointer = civilianairport[this.dialogData.ReversePointer as keyof CivilianAirportDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(civilianairport)
            }
            this.selection = new SelectionModel<CivilianAirportDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, CivilianAirportDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to CivilianAirportDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as CivilianAirportDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let civilianairport = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as CivilianAirportDB
              this.initialSelection.push(civilianairport)
            }
          }

          this.selection = new SelectionModel<CivilianAirportDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.civilianairports
      }
    )
  }

  // newCivilianAirport initiate a new civilianairport
  // create a new CivilianAirport objet
  newCivilianAirport() {
  }

  deleteCivilianAirport(civilianairportID: number, civilianairport: CivilianAirportDB) {
    // list of civilianairports is truncated of civilianairport before the delete
    this.civilianairports = this.civilianairports.filter(h => h !== civilianairport);

    this.civilianairportService.deleteCivilianAirport(civilianairportID, this.GONG__StackPath).subscribe(
      civilianairport => {
        this.civilianairportService.CivilianAirportServiceChanged.next("delete")
      }
    );
  }

  editCivilianAirport(civilianairportID: number, civilianairport: CivilianAirportDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(civilianairportID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "civilianairport" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, civilianairportID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.civilianairports.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.civilianairports.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<CivilianAirportDB>()

      // reset all initial selection of civilianairport that belong to civilianairport
      for (let civilianairport of this.initialSelection) {
        let index = civilianairport[this.dialogData.ReversePointer as keyof CivilianAirportDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(civilianairport)

      }

      // from selection, set civilianairport that belong to civilianairport
      for (let civilianairport of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = civilianairport[this.dialogData.ReversePointer as keyof CivilianAirportDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(civilianairport)
      }


      // update all civilianairport (only update selection & initial selection)
      for (let civilianairport of toUpdate) {
        this.civilianairportService.updateCivilianAirport(civilianairport, this.GONG__StackPath)
          .subscribe(civilianairport => {
            this.civilianairportService.CivilianAirportServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, CivilianAirportDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedCivilianAirport = new Set<number>()
      for (let civilianairport of this.initialSelection) {
        if (this.selection.selected.includes(civilianairport)) {
          // console.log("civilianairport " + civilianairport.Name + " is still selected")
        } else {
          console.log("civilianairport " + civilianairport.Name + " has been unselected")
          unselectedCivilianAirport.add(civilianairport.ID)
          console.log("is unselected " + unselectedCivilianAirport.has(civilianairport.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let civilianairport = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as CivilianAirportDB
      if (unselectedCivilianAirport.has(civilianairport.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<CivilianAirportDB>) = new Array<CivilianAirportDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          civilianairport => {
            if (!this.initialSelection.includes(civilianairport)) {
              // console.log("civilianairport " + civilianairport.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + civilianairport.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = civilianairport.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = civilianairport.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("civilianairport " + civilianairport.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<CivilianAirportDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
