// generated by gong
import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { OrderDB } from '../order-db'
import { OrderService } from '../order.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-order-sorting',
  templateUrl: './order-sorting.component.html',
  styleUrls: ['./order-sorting.component.css']
})
export class OrderSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of Order instances that are in the association
  associatedOrders = new Array<OrderDB>();

  constructor(
    private orderService: OrderService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of order instances
    public dialogRef: MatDialogRef<OrderSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getOrders()
  }

  getOrders(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let order of this.frontRepo.Orders_array) {
          let ID = this.dialogData.ID
          let revPointerID = order[this.dialogData.ReversePointer]
          let revPointerID_Index = order[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedOrders.push(order)
          }
        }

        // sort associated order according to order
        this.associatedOrders.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedOrders, event.previousIndex, event.currentIndex);

    // set the order of Order instances
    let index = 0
    
    for (let order of this.associatedOrders) {
      let revPointerID_Index = order[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedOrders.forEach(
      order => {
        this.orderService.updateOrder(order)
          .subscribe(order => {
            this.orderService.OrderServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
