// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, NullInt64, SelectionMode } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { OrderDB } from '../order-db'
import { OrderService } from '../order.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-orderstable',
  templateUrl: './orders-table.component.html',
  styleUrls: ['./orders-table.component.css'],
})
export class OrdersTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode

  // used if the component is called as a selection component of Order instances
  selection: SelectionModel<OrderDB>;
  initialSelection = new Array<OrderDB>();

  // the data source for the table
  orders: OrderDB[];
  matTableDataSource: MatTableDataSource<OrderDB>

  // front repo, that will be referenced by this.orders
  frontRepo: FrontRepo

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (orderDB: OrderDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return orderDB.Name;

        case 'Duration':
          return orderDB.Duration;

        case 'OrderMessage':
          return orderDB.OrderMessage;

        case 'Number':
          return orderDB.Number;

        case 'Type':
          return orderDB.Type;

        case 'Target':
          return (orderDB.Target ? orderDB.Target.Name : '');

        case 'TargetLat':
          return orderDB.TargetLat;

        case 'TargetLng':
          return orderDB.TargetLng;

        default:
          return OrderDB[property];
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (orderDB: OrderDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the orderDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += orderDB.Name.toLowerCase()
      mergedContent += orderDB.OrderMessage.toLowerCase()
      mergedContent += orderDB.Number.toString()
      mergedContent += orderDB.Type.toLowerCase()
      if (orderDB.Target) {
        mergedContent += orderDB.Target.Name.toLowerCase()
      }
      mergedContent += orderDB.TargetLat.toString()
      mergedContent += orderDB.TargetLng.toString()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort;
    this.matTableDataSource.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private orderService: OrderService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of order instances
    public dialogRef: MatDialogRef<OrdersTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.orderService.OrderServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getOrders()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Duration",
        "OrderMessage",
        "Number",
        "Type",
        "Target",
        "TargetLat",
        "TargetLng",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Duration",
        "OrderMessage",
        "Number",
        "Type",
        "Target",
        "TargetLat",
        "TargetLng",
      ]
      this.selection = new SelectionModel<OrderDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getOrders()
    this.matTableDataSource = new MatTableDataSource(this.orders)
  }

  getOrders(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.orders = this.frontRepo.Orders_array;

        // insertion point for variables Recoveries
        // compute strings for durations
        for (let order of this.orders) {
          order.Duration_string =
            Math.floor(order.Duration / (3600 * 1000 * 1000 * 1000)) + "H " +
            Math.floor(order.Duration % (3600 * 1000 * 1000 * 1000) / (60 * 1000 * 1000 * 1000)) + "M " +
            order.Duration % (60 * 1000 * 1000 * 1000) / (1000 * 1000 * 1000) + "S"
        }

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          this.orders.forEach(
            order => {
              let ID = this.dialogData.ID
              let revPointer = order[this.dialogData.ReversePointer]
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(order)
              }
            }
          )
          this.selection = new SelectionModel<OrderDB>(allowMultiSelect, this.initialSelection);
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

          if (sourceInstance[this.dialogData.SourceField]) {
            for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
              let order = associationInstance[this.dialogData.IntermediateStructField]
              this.initialSelection.push(order)
            }
          }
          this.selection = new SelectionModel<OrderDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.orders
      }
    )
  }

  // newOrder initiate a new order
  // create a new Order objet
  newOrder() {
  }

  deleteOrder(orderID: number, order: OrderDB) {
    // list of orders is truncated of order before the delete
    this.orders = this.orders.filter(h => h !== order);

    this.orderService.deleteOrder(orderID).subscribe(
      order => {
        this.orderService.OrderServiceChanged.next("delete")
      }
    );
  }

  editOrder(orderID: number, order: OrderDB) {

  }

  // display order in router
  displayOrderInRouter(orderID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongfly_go-" + "order-display", orderID])
  }

  // set editor outlet
  setEditorRouterOutlet(orderID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongfly_go_editor: ["github_com_fullstack_lang_gongfly_go-" + "order-detail", orderID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(orderID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongfly_go_presentation: ["github_com_fullstack_lang_gongfly_go-" + "order-presentation", orderID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.orders.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.orders.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<OrderDB>()

      // reset all initial selection of order that belong to order
      this.initialSelection.forEach(
        order => {
          order[this.dialogData.ReversePointer].Int64 = 0
          order[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(order)
        }
      )

      // from selection, set order that belong to order
      this.selection.selected.forEach(
        order => {
          let ID = +this.dialogData.ID
          order[this.dialogData.ReversePointer].Int64 = ID
          order[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(order)
        }
      )

      // update all order (only update selection & initial selection)
      toUpdate.forEach(
        order => {
          this.orderService.updateOrder(order)
            .subscribe(order => {
              this.orderService.OrderServiceChanged.next("update")
            });
        }
      )
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedOrder = new Set<number>()
      for (let order of this.initialSelection) {
        if (this.selection.selected.includes(order)) {
          // console.log("order " + order.Name + " is still selected")
        } else {
          console.log("order " + order.Name + " has been unselected")
          unselectedOrder.add(order.ID)
          console.log("is unselected " + unselectedOrder.has(order.ID))
        }
      }

      // delete the association instance
      if (sourceInstance[this.dialogData.SourceField]) {
        for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
          let order = associationInstance[this.dialogData.IntermediateStructField]
          if (unselectedOrder.has(order.ID)) {

            this.frontRepoService.deleteService( this.dialogData.IntermediateStruct, associationInstance )
          }
        }
      }

      // is the source array is emptyn create it
      if (sourceInstance[this.dialogData.SourceField] == undefined) {
        sourceInstance[this.dialogData.SourceField] = new Array<any>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField]) {
        this.selection.selected.forEach(
          order => {
            if (!this.initialSelection.includes(order)) {
              // console.log("order " + order.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + order.Name,
              }

              associationInstance[this.dialogData.IntermediateStructField+"ID"] = new NullInt64
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Int64 = order.ID
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Valid = true

              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"] = new NullInt64
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Int64 = sourceInstance["ID"]
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Valid = true

              this.frontRepoService.postService( this.dialogData.IntermediateStruct, associationInstance )

            } else {
              // console.log("order " + order.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<OrderDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
