// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, NullInt64, SelectionMode } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { ReportDB } from '../report-db'
import { ReportService } from '../report.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-reportstable',
  templateUrl: './reports-table.component.html',
  styleUrls: ['./reports-table.component.css'],
})
export class ReportsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode

  // used if the component is called as a selection component of Report instances
  selection: SelectionModel<ReportDB>;
  initialSelection = new Array<ReportDB>();

  // the data source for the table
  reports: ReportDB[];
  matTableDataSource: MatTableDataSource<ReportDB>

  // front repo, that will be referenced by this.reports
  frontRepo: FrontRepo

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort) sort: MatSort;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (reportDB: ReportDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return reportDB.Name;

        case 'Duration':
          return reportDB.Duration;

        case 'ReportMessage':
          return reportDB.ReportMessage;

        case 'Number':
          return reportDB.Number;

        case 'Type':
          return reportDB.Type;

        case 'About':
          return (reportDB.About ? reportDB.About.Name : '');

        case 'OpsLine':
          return (reportDB.OpsLine ? reportDB.OpsLine.Name : '');

        default:
          return ReportDB[property];
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (reportDB: ReportDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the reportDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += reportDB.Name.toLowerCase()
      mergedContent += reportDB.ReportMessage.toLowerCase()
      mergedContent += reportDB.Number.toString()
      mergedContent += reportDB.Type.toLowerCase()
      if (reportDB.About) {
        mergedContent += reportDB.About.Name.toLowerCase()
      }
      if (reportDB.OpsLine) {
        mergedContent += reportDB.OpsLine.Name.toLowerCase()
      }

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort;
    this.matTableDataSource.paginator = this.paginator;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private reportService: ReportService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of report instances
    public dialogRef: MatDialogRef<ReportsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.reportService.ReportServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getReports()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "Duration",
        "ReportMessage",
        "Number",
        "Type",
        "About",
        "OpsLine",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Duration",
        "ReportMessage",
        "Number",
        "Type",
        "About",
        "OpsLine",
      ]
      this.selection = new SelectionModel<ReportDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getReports()
    this.matTableDataSource = new MatTableDataSource(this.reports)
  }

  getReports(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.reports = this.frontRepo.Reports_array;

        // insertion point for variables Recoveries
        // compute strings for durations
        for (let report of this.reports) {
          report.Duration_string =
            Math.floor(report.Duration / (3600 * 1000 * 1000 * 1000)) + "H " +
            Math.floor(report.Duration % (3600 * 1000 * 1000 * 1000) / (60 * 1000 * 1000 * 1000)) + "M " +
            report.Duration % (60 * 1000 * 1000 * 1000) / (1000 * 1000 * 1000) + "S"
        }

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          this.reports.forEach(
            report => {
              let ID = this.dialogData.ID
              let revPointer = report[this.dialogData.ReversePointer]
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(report)
              }
            }
          )
          this.selection = new SelectionModel<ReportDB>(allowMultiSelect, this.initialSelection);
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

          if (sourceInstance[this.dialogData.SourceField]) {
            for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
              let report = associationInstance[this.dialogData.IntermediateStructField]
              this.initialSelection.push(report)
            }
          }
          this.selection = new SelectionModel<ReportDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.reports
      }
    )
  }

  // newReport initiate a new report
  // create a new Report objet
  newReport() {
  }

  deleteReport(reportID: number, report: ReportDB) {
    // list of reports is truncated of report before the delete
    this.reports = this.reports.filter(h => h !== report);

    this.reportService.deleteReport(reportID).subscribe(
      report => {
        this.reportService.ReportServiceChanged.next("delete")
      }
    );
  }

  editReport(reportID: number, report: ReportDB) {

  }

  // display report in router
  displayReportInRouter(reportID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongfly_go-" + "report-display", reportID])
  }

  // set editor outlet
  setEditorRouterOutlet(reportID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongfly_go_editor: ["github_com_fullstack_lang_gongfly_go-" + "report-detail", reportID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(reportID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongfly_go_presentation: ["github_com_fullstack_lang_gongfly_go-" + "report-presentation", reportID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.reports.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.reports.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<ReportDB>()

      // reset all initial selection of report that belong to report
      this.initialSelection.forEach(
        report => {
          report[this.dialogData.ReversePointer].Int64 = 0
          report[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(report)
        }
      )

      // from selection, set report that belong to report
      this.selection.selected.forEach(
        report => {
          let ID = +this.dialogData.ID
          report[this.dialogData.ReversePointer].Int64 = ID
          report[this.dialogData.ReversePointer].Valid = true
          toUpdate.add(report)
        }
      )

      // update all report (only update selection & initial selection)
      toUpdate.forEach(
        report => {
          this.reportService.updateReport(report)
            .subscribe(report => {
              this.reportService.ReportServiceChanged.next("update")
            });
        }
      )
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s"]
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedReport = new Set<number>()
      for (let report of this.initialSelection) {
        if (this.selection.selected.includes(report)) {
          // console.log("report " + report.Name + " is still selected")
        } else {
          console.log("report " + report.Name + " has been unselected")
          unselectedReport.add(report.ID)
          console.log("is unselected " + unselectedReport.has(report.ID))
        }
      }

      // delete the association instance
      if (sourceInstance[this.dialogData.SourceField]) {
        for (let associationInstance of sourceInstance[this.dialogData.SourceField]) {
          let report = associationInstance[this.dialogData.IntermediateStructField]
          if (unselectedReport.has(report.ID)) {

            this.frontRepoService.deleteService( this.dialogData.IntermediateStruct, associationInstance )
          }
        }
      }

      // is the source array is emptyn create it
      if (sourceInstance[this.dialogData.SourceField] == undefined) {
        sourceInstance[this.dialogData.SourceField] = new Array<any>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField]) {
        this.selection.selected.forEach(
          report => {
            if (!this.initialSelection.includes(report)) {
              // console.log("report " + report.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + report.Name,
              }

              associationInstance[this.dialogData.IntermediateStructField+"ID"] = new NullInt64
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Int64 = report.ID
              associationInstance[this.dialogData.IntermediateStructField+"ID"].Valid = true

              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"] = new NullInt64
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Int64 = sourceInstance["ID"]
              associationInstance[this.dialogData.SourceStruct + "_" + this.dialogData.SourceField + "DBID"].Valid = true

              this.frontRepoService.postService( this.dialogData.IntermediateStruct, associationInstance )

            } else {
              // console.log("report " + report.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<ReportDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
